import React,{ Component }  from 'react';
import axios from 'axios';
import './App.css';
import { Container } from 'semantic-ui-react';
import Header from './components/Header/Header';
import Tweet from './components/Tweet/Tweet';

class App extends Component {
  constructor (props) {
    super(props)
    this.state = {
      tweets: [],
      term: ''
    }
  }
  componentDidMount() {
    axios.get(`http://localhost:3001/api/v1/tweets`)
      .then(res => {
        const tweets = res.data;
        this.setState({ tweets });
      })
  }

  searchUserName = (newTerm) => {
    axios.post('/timeline', null, {
      user_name: newTerm
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(JSON.stringify(error));
    });
  }
  render() {
    let tweets = this.state.tweets.reverse()
    let tweetCards = tweets.map((tweet) => {
      return <Tweet key={tweet.id} tweet={tweet} />
    })
    return (
      <>
      <div className="App">
        <header className="App-header">
          <p>
            Demo App using React, NodeJS and Twitter API
          </p>
        </header>
      </div>,
      <Header searchUserName={this.searchUserName}/>
      <Container className="dashboard-container">
        {tweetCards}
      </Container>
    </>
    );
  }

  }


export default App;
