import React, { useEffect, useState, Fragment } from 'react';
import axios from 'axios';
import './App.css';
import { Container } from 'semantic-ui-react';
import Header from './components/Header/Header';
import Tweet from './components/Tweet/Tweet';
import consumeTwitterApi from './consumeTwitterApi';

function App() {
  const [query, setQuery] = useState('bhtomar');
  const [{ data, isLoading, isError }, doFetch] = consumeTwitterApi();
  var tweetCards = [];

  function refreshTweets() {
    tweetCards = tweets.map ? tweets.map((tweet) => {
      return <Tweet key={tweet.id} tweet={tweet} />
    }): [];
  }




  return ([refreshTweets(),
    <Fragment>
      <div className="App">
        <header className="App-header">
          <p>
            Demo App using React, NodeJS and Twitter API
        </p>
        </header>
      </div>,
    <>
    <form onSubmit={event => {
        doFetch(`http://localhost:3001/api/v1/timeline?screen_name=${query}`);

        event.preventDefault();
      }}>
        <input
          type="text"
          value={query}
          onChange={event => setQuery(event.target.value)}
        />
        <button type="submit">Search</button>
      </form>
        {isLoading ? (
          <div>Loading ...</div>
        ) : (
            <Container className="dashboard-container">
              {tweetCards}
            </Container>
          )}
      </>

    </Fragment>
  ]);
}
