          onChange={event => setQuery(event.target.value)} placeholder='View tweets by user' fluid />
          <Button color={'teal'} className="search-field" fluid onClick={() => {
            props.searchUserName = document.getElementById('searchInput').value;
          }}>Search</Button>
      </Container>
      <Container className='header-container'>
          <Input focus id='filterInput' className="search-field" placeholder='Filter tweets by...' fluid />
          <Button color={'teal'} className="search-field" fluid onClick={() => {
            props.updateFilterTerm = document.getElementById('filterInput').value;
          }}>Filter Results</Button>
      </Container>
      <Container>
      {/* <Message >
        <p>
          You are viewing tweets containing <strong>"{props.term}"</strong> word in them.
        </p>
      </Message> */}
      </Container>
      <Divider />
    </div>
  )
}

Header.propTypes = {
  updateFilterTerm: PropTypes.string.isRequired,
  searchUserName: PropTypes.string.isRequired
}

export default Header
