  const[data, setData] = useState({tweets: []})
  let tweetCards = [];
  let newTerm = 'bhtomar'

  useEffect(async () => {
    axios.get('http://localhost:3001/api/v1/timeline', null, { params: {
      user_name: newTerm
    } 
    })
    .then(function (response) {
      //console.log(response.data);
      let userTweets = response.data;
      setData(userTweets);
      tweetCards = tweets.map((tweet) => {
        return <Tweet key={tweet.id} tweet={tweet} />
      })
    })
    .catch(function (error) {
      console.log(JSON.stringify(error));
    });
  }, []);

