import React from 'react'
import { Item, Image } from 'semantic-ui-react'
import PropTypes from 'prop-types'

const Tweet = props => {
  const { user, text } = props.tweet
  return (
    <Item raised centered fluid>
      <Item.Content>
        <Image floated='left' size='mini' src={user.profile_image_url} />
        <Item.Header>{user.name}</Item.Header>
        <Item.Meta><a href={`https://twitter.com/${user.screen_name}`} target='_blank'>{`@${user.screen_name}`}</a></Item.Meta>
        <Item.Description>
          {text}
        </Item.Description>
        <Item.Extra>{created_at}</Item.Extra>
      </Item.Content>
    </Item>
  )
}

Tweet.propTypes = {
  tweet: PropTypes.object.isRequired
}

export default Tweet
