import React from 'react'
import { Button, Container, Input, Divider } from 'semantic-ui-react'
import PropTypes from 'prop-types'
import './Header.css'

const Header = props => {
  return (
    <div className='search-container'>
      <Container className='header-container'>
          <Input focus id='searchInput' className="search-field" placeholder='View tweets by @user' fluid />
          <Button color={'teal'} className="search-field" fluid onClick={() => {
            props.updateSearchTerm(document.getElementById('searchInput').value)
          }}>Search</Button>
      </Container>
      <Container className='header-container'>
          <Input focus id='filterInput' className="search-field" placeholder='Filter tweets by...' fluid />
          <Button color={'teal'} className="search-field" fluid onClick={() => {
            props.updateFilterTerm(document.getElementById('filterInput').value)
          }}>Filter Results</Button>
      </Container>
      <Container>
      {/* <Message >
        <p>
          You are viewing tweets containing <strong>"{props.term}"</strong> word in them.
        </p>
      </Message> */}
      </Container>
      <Divider />
    </div>
  )
}

Header.propTypes = {
  term: PropTypes.string.isRequired,
  updateSearchTerm: PropTypes.func.isRequired
}

export default Header
