import React, { useEffect, useState, Fragment } from 'react';
import axios from 'axios';
import './App.css';
import { Container } from 'semantic-ui-react';
import Header from './components/Header/Header';
import Tweet from './components/Tweet/Tweet';

function App() {
  const [tweets, setTweets] = useState({ tweets: [] });
  const [query, setQuery] = useState('bhtomar');
  const [search, setSearch] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  var tweetCards = [];

  function refreshTweets() {
    tweetCards = tweets.map ? tweets.map((tweet) => {
      return <Tweet key={tweet.id} tweet={tweet} />
    }): [];
  }

  useEffect(async () => {
    const fetchTweets = async () => {
      setIsLoading(true);
      const result = await axios.get(`http://localhost:3001/api/v1/timeline?screen_name=${query}`);
      //console.log(result);
      setTweets(result.data);
      setIsLoading(false);

    };
    fetchTweets();
    refreshTweets();

  }, [query]);



  return (
    <Fragment>
      <div className="App">
        <header className="App-header">
          <p>
            Demo App using React, NodeJS and Twitter API
        </p>
        </header>
      </div>,
    <>
        <input
          type="text"
          value={query}
          onChange={event => setQuery(event.target.value)}
        />
        <button type="button" onClick={() => setSearch(query)}>
          Search
      </button>
        {isLoading ? (
          <div>Loading ...</div>
        ) : (
            <Container className="dashboard-container">
              {tweetCards}
            </Container>
          )}
      </>

    </Fragment>
  );
}
